              case UP -> System.out.println("往上走");
              case DOWN ->System.out.println("往下走");
              case LEFT -> System.out.println("往左走");
              case RIGHT -> System.out.println("往右走");
          }
      }
      public static void main(String[] args) {
          move(Orientation.RIGHT);
      }
