def buquan(message,uid):
    #计算括号数量并自动补全
    def countBracket(message):
        countban=0
        for i in message:
            if i=='(':
                countban+=1
            if i==')':
                countban-=1
        countquan=0
        for i in message:
            if i=='（':
                countquan+=1
            if i=='）':
                countquan-=1
        return {'ban':countban,'quan':countquan}
    #自动补全括号
    ban=countBracket(message)['ban']
    quan=countBracket(message)['quan']
    bu=""
    if not (ban or quan):
        return
    if ban>0:
        for i in range(ban):
            bu+=')'
    elif ban<0:
        for i in range(-ban):
            bu+='('
    if quan>0:
        for i in range(quan):
            bu+='）'
    elif quan<0:
        for i in range(-quan):
