### 线性表顺序结构实现

```C++
//数组静态分配
typedef struct{
  char name[100];
  int num;
  float sum;
}ElementType;
typedef struct{
	ElementType data[MaxSize];
	int length;
}SqList;

//数组动态分配
typedef struct{
  char name[100];
  int num;
  float sum;
}ElementType;
typedef struct{
	ElementType *data;
	int length
}SqList;
SqList L;
L.data=(ElementType*)malloc(sizeof(ElementType)*MaxSize);
//#include <cstdlib>
//malloc(m)函数，开辟m字节长度的地址空间，并返回这段空间的首地址
//sizeof(x)运算，计算变量x的长度
//free(p)函数，释放指针p所指变量的存储空间，彻底删除一个变量
//typedef type name 定义种类为type的变量种类


//各种功能实现
//函数结果状态代码
#define TRUE 1
#define FALSE 0
#define OK 1
#define ERROR 1
#define INFEASIBLE -1
#define OVERFLOW -2
//Status是所有函数的类型，值是函数结果状态代码
typedef int Status;

//线性表初始化
Status InitList(SqList &L){
  L.data=new ElementType[MAXSIZE];
  if(!L.data)
    exit(OVERFLOW);//内存开辟失败
  L.length=0;
  return OK;
}
void DestoryList(SqList &L){
  if(L.data)
    delete L.data;
}
void ClearList(SqList &L){
  L.length=0;
}
int GetLength(SqList L){
	return L.length;  
}
bool IsEmpty(SqList L){
  if(L.length==0)
    return true;
  return false;
}
int GetElement(SqList L,int i,ElementType &e){
	if(i>=1&&i<=L.length){
    e=L.data[i-1];
    return OK;
  }
  return ERROR;
}
int FindElementByNum(SqList L,int num){
  for(int i=0;i<L.length;i++){
    if(L.data[i]==num){
      return i+1;
    }
  }
  return 0;
}
Status Insert(SqList &L,int i,ElementType e){
  if(i<1||i>L.length+1)
    return ERROR;
  if(L.length==MAXSIZE)
    return ERROR;
  for(int j=L.length-1;j>=i-1;j--){
    L.data[j+1]=L.data[j];
  }
  L.data[i-1]=e;
  L.length++;
  return OK;
}
Status Delete(SqList &L,int i,ElementType &e){
  if(i<1||i>L.length+1)
    return ERROR;
  e=L.data[i-1]
  for(int j=L.length-1;i<=j;i++){
    L.data[i-1]=L.data[i];
  }
  L.length--;
  return OK;
}
```

### 线性表链式结构实现

```C++
typedef struct{
	int num;
  char name[10];
  int score;
}ElementType;
typedef struct Lnode{
	ElementType data;
	struct Lnode *next;
}Lnode,*LinkList;
LinkList L;<==>LNode *p
Status InitList(LinkList &L){
  L=new LNode;
  L->next=NULL;
  return OK;
}
bool IsEmpty(LinkList L){
  if(L->next==NULL)
  	return 1;
  return 0;
}
Status DestoryList(LinkList &L){
  LNode *p;
  while(L!=NULL){
    p=L;
    L=L->next;
    delete p;
  }
  return OK;
}
Status ClearList(LinkList &L){
  LNode *p,*q;
  p=L->next;
  while(L!=NULL){
    q=p->next;
    delete p;
    p=q;
  }
  L->next=NULL;
  return OK;
}
int ListLength(LinkList L){
  int i=0;
  LinkList p=L->next;
  while(p!=NULL){
    i++;
    p=p->next;
  }
  return i;
}
int GetElement(LinkList L,int i,ElementType &e){
	if(i<1||i>L.length)
    return ERROR;
  LinkList p=L;
  for(int j=1;j<=i;j++){
    p=p->next;
  }
  e=p->data;
  return OK;
}
*LNode FindElementGetAddress(LinkList L,ElementType e){
  LinkList p=L->next;
  while(p&&p->data!=e){
    p=p->next;
  }
  return p;
}
int FindElementGetNum(LinkList L,ElementType e){
  LinkList p=L->next;
  int j=1;
  whlie(p&&p->data!=e){
    p=p->next;
    j++;
  }
  if(p)
    return j;
  return 0
}
Status Insert(LinkList &L,int i,ElementType e){
  LinkList p=L;
  LinkList s;
  int j=1
  s->data=e;
  whlie(p&&j<=i-1){
    j++;
    p=p->next;
  }
  if(!p)
    return ERROR;
  LinkList s->next=p->next;
  p->next=s;
  return OK;
}
Status Delete(LinkList &L,int i,ElementType &e){
  LinkList p=L,s;
  whlie(p&&j<=i-1){
    j++;
    p=p->next;
  }
  if(!(p->next)||j>i-1)
    return ERROE
  s=p->next;
  p->next=s->next;
  e=s->data
  delete s;
  return OK
}
//单链表建立方法：头插法
//1、建立一个空表，重复读入数据
//2、生成新节点，将读入数据存放到新节点的数据域中
//3、从最后一个节点开始，依次将各节点插入到链表的前端
L=new LNode;
p=new LNode;
p->data=a;
p->next=L->next;
L->next=p;

//单链表建立方法：头插法

void CreateList_R(LinkList &L,int n){
	L=new LNode;
  L->next=NULL;
  r=L;
  for(int i=0;i<n;i++){
    p=new LNode;
    cin>>p->data;
    p->next=NULL;
    r->next=p;
    r=p;
  }
}
p->data=a;
p->next=NULL;
r->next=p;//尾指针
r=p

//循环链表
//好处：从表中任意阶段出发均可找到表中其他节点
LinkList Connect(LinkList &a,LinkList &b){
  LinkList p=a->next;
  a->next=b->next->next
  delete b->next;
  b->next=p;
  return b;
}
//双向链表
typedef struct DuLNode{
  ElementType data;
  struct DuLNode *prior,*next;
}DuLNode,*DuLinkList;

//线性表合并
void Union(List &La,List Lb){
  La_len=ListLength(La);
  Lb_len=ListLength(Lb);
  for(int i=1;i<=Lb_len;i++){
    LNode e;
    GetElement(Lb,i,e);
    if(!FindElement(La,e))
    	Insert(&La,++La_len,e);
  }
}
//有序表合并
void Union(LinkList &La,LinkList &Lb,LinkList &Lc){
  pa=La->next;
  pb=Lb->next;
  pc=Lc=La;
  while(pa&&pb){
    if(pa->data<=pb->data){
      pc->next=pa;
      pc=pa;
      pa=pa->next;
    }
    else{
      pc->next=pb;
      pc=pb;
      pb=pb->next;
    }
    pc->next=pa?pa:pb;
    delete Lb;
  }
}





















